svg.script:
let delay = (ms) => new Promise(res => setTimeout(res, ms));
async function animate() {
  const skills = [line1,line2,line3,line4,line5,line6,line7,line8,line9];
  const other = [line10,line11,line12,line13,line14,line15];
  while (true) {
    // Show skills
    for (let i = 0; i < skills.length; i++) {
      skills[i].setAttribute("class", "text");
      await delay(400);
    }
    await delay(1500);
    // Hide skills
    for (let i = 0; i < skills.length; i++) {
      skills[i].setAttribute("class", "text hide");
    }
    await delay(500);
    // Show other
    for (let i = 0; i < other.length; i++) {
      other[i].setAttribute("class", "text");
      await delay(400);
    }
    await delay(3000);
    for (let i = 0; i < other.length; i++) {
      other[i].setAttribute("class", "text hide");
    }
    await delay(500);
  }
}
animate();
